mapper.select:
SELECT ORDER_NUMBER, USER_NUMBER, ORDER_RECIPIENT, ORDER_ZIPCODE, ORDER_ADDRESS, ORDER_ADDRESS_DETAIL, ORDER_CELLPHONENUMBER, ORDER_PHONENUMBER, ORDER_DATE, ORDER_STATUS_NUMBER, USER_NAME, PAYMENT_TOTAL_AMOUNT FROM( SELECT ROWNUM AS RNUM, ORDER_NUMBER, USER_NUMBER, ORDER_RECIPIENT, ORDER_ZIPCODE, ORDER_ADDRESS, ORDER_ADDRESS_DETAIL, ORDER_CELLPHONENUMBER, ORDER_PHONENUMBER, ORDER_DATE, ORDER_STATUS_NUMBER, USER_NAME, PAYMENT_TOTAL_AMOUNT FROM( select O.ORDER_NUMBER, O.USER_NUMBER, O.ORDER_RECIPIENT, O.ORDER_ZIPCODE, O.ORDER_ADDRESS, O.ORDER_ADDRESS_DETAIL, O.ORDER_CELLPHONENUMBER, O.ORDER_PHONENUMBER, O.ORDER_DATE, O.ORDER_STATUS_NUMBER, U.USER_NAME, P.PAYMENT_TOTAL_AMOUNT from JUMAK_ORDER O JOIN JUMAK_USER U ON O.USER_NUMBER = U.USER_NUMBER LEFT JOIN JUMAK_PAYMENT P ON O.ORDER_NUMBER = P.ORDER_NUMBER ORDER BY ORDER_NUMBER DESC)
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}

mapper.update:
update JUMAK_ORDER
set ORDER_STATUS_NUMBER = #{orderStatusNumber}
where ORDER_NUMBER = #{orderNumber}
