mapper.select:
SELECT COUNT(USER_NUMBER)
FROM JUMAK_USER
WHERE USER_NUMBER = #{userNumber} AND USER_PASSWORD = #{userPassword}

mapper.update:
UPDATE JUMAK_USER SET
USER_PASSWORD = #{newPassword},
USER_NICKNAME = #{newNickname} , USER_EMAIL = #{newEmail}, USER_PHONENUMBER = #{newPhoneNumber}, USER_ADDRESS = #{newAddress}, USER_ADDRESS_DETAIL = #{newAddressDetail}, USER_ZIPCODE = #{newZipcode} WHERE USER_NUMBER = #{userNumber}
