mapper.select:
SELECT PRODUCT_NUMBER, PRODUCT_CATEGORY_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_SUBTITLE, PRODUCT_ALCOHOLICITY, PRODUCT_NET_WEIGHT, PRODUCT_RELEASE_DATE, PRODUCT_SALES, PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID FROM( SELECT ROWNUM RNUM, PRODUCT_NUMBER, PRODUCT_CATEGORY_NUMBER, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_SUBTITLE, PRODUCT_ALCOHOLICITY, PRODUCT_NET_WEIGHT, PRODUCT_RELEASE_DATE, PRODUCT_SALES, PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID FROM( SELECT P.PRODUCT_NUMBER, P.PRODUCT_CATEGORY_NUMBER, P.PRODUCT_NAME, P.PRODUCT_PRICE, P.PRODUCT_SUBTITLE, P.PRODUCT_ALCOHOLICITY, P.PRODUCT_NET_WEIGHT, P.PRODUCT_RELEASE_DATE, P.PRODUCT_SALES, I.PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID FROM JUMAK_PRODUCT P LEFT JOIN JUMAK_PRODUCT_IMG_MAIN I ON P.PRODUCT_NUMBER = I.PRODUCT_NUMBER
PRODUCT_CATEGORY_NUMBER IN(1,2)
PRODUCT_CATEGORY_NUMBER IN(3,4)
PRODUCT_CATEGORY_NUMBER IN(5,6)
PRODUCT_CATEGORY_NUMBER IN(7,8)
PRODUCT_CATEGORY_NUMBER = 9
ORDER BY PRODUCT_RELEASE_DATE DESC
ORDER BY PRODUCT_SALES DESC
ORDER BY PRODUCT_PRICE
ORDER BY PRODUCT_PRICE DESC
)
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}
