mapper.select:
SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_CATEGORY_NUMBER,PRODUCT_CATEGORY_NAME, PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID FROM ( SELECT ROWNUM AS RNUM, PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_CATEGORY_NUMBER,PRODUCT_CATEGORY_NAME, PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID FROM ( SELECT P.PRODUCT_NUMBER, P.PRODUCT_NAME, P.PRODUCT_CATEGORY_NUMBER, Y.PRODUCT_CATEGORY_NUMBER,Y.PRODUCT_CATEGORY_NAME, J.PRODUCT_IMG_MAIN_NUMBER, J.PRODUCT_IMG_MAIN_NAME, J.PRODUCT_IMG_MAIN_PATH, J.PRODUCT_IMG_MAIN_UUID FROM JUMAK_PRODUCT P JOIN JUMAK_PRODUCT_CATEGORY Y ON P.PRODUCT_CATEGORY_NUMBER = Y.PRODUCT_CATEGORY_NUMBER LEFT JOIN (
SELECT PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID, PRODUCT_NUMBER FROM ( SELECT PRODUCT_IMG_MAIN_NUMBER, PRODUCT_IMG_MAIN_NAME, PRODUCT_IMG_MAIN_PATH, PRODUCT_IMG_MAIN_UUID, PRODUCT_NUMBER, RANK() OVER(PARTITION BY PRODUCT_NUMBER ORDER BY PRODUCT_IMG_MAIN_NUMBER) RK FROM JUMAK_PRODUCT_IMG_MAIN J ) WHERE RK = 1 ) J ON J.PRODUCT_NUMBER= J.PRODUCT_NUMBER ORDER BY PRODUCT_NUMBER DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}
